import React, {useEffect, useState} from "react";
import TuitListItem from "./tuit-list-item";
import tuits from '../tuit-list/tuits.json';
import './tuits.css';
import {useDispatch, useSelector}
    from "react-redux";
import {createTuit,deleteTuit, findAllTuits} from "../actions/tuits-actions";


const TuitList = () => {
    const tuits
        = useSelector(state => state.tuits);
    const dispatch = useDispatch();
    useEffect(() =>
            findAllTuits(dispatch),
        deleteTuit(dispatch),
        []);
    const [newTuit, setNewTuit] =
        useState({tuit: 'New tuit'});



    return (

        <ul className="ttr-tuits list-group">

            <button onClick={() =>
                createTuit(dispatch, newTuit)}
                    className="btn btn-primary float-end">
                Tuit
            </button>
            <textarea className="form-control w-75"
                      onChange={(e) =>
                          setNewTuit({...newTuit,
                              tuit: e.target.value})}></textarea>

            {

                tuits.map && tuits.map(tuit =>
                <>
                    <i className="fas fa-remove float-end" onClick={() => deleteTuit(dispatch, tuit)}></i>
                    <TuitListItem key={tuit._id}
                                  tuit={tuit}/>
                </>)
            }

        </ul>

    );
}

export default TuitList;
