import tuits from '../tuit-list/tuits.json';
import {DELETE_TUIT, FIND_ALL_TUITS}
    from "../actions/tuits-actions";


const tuitsReducer = (state = [], action) => {
    switch (action.type) {
        case FIND_ALL_TUITS:
            return action.tuits;
        default:
            return state;
    }
}


export default tuitsReducer;
