import React from "react";


const TuitListItem = ({tuit={
    "_id": "123",
    "topic": "Web Development",
    "postedBy": {
        "username": "ReactJS"
    },
    "liked": true,
    "verified": false,
    "handle": "ReactJS",
    "time": "2h",
    "title": "React.js is a component based front end library that makes it very easy to build Single Page Applications or SPAs",
    "tuit": "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.",
    "attachments": {
        "video": "unKvMC3Y1kI"
    },
    "logo_image": "../../../../tuiter/countdown.jpg",
    "avatar_image": "../../../../tuiter/Elon_Musk.jpg",
    "comments": 123,
    "retuits": 234,
    "likes": 345
}})=> {


    return (
        <>
            <div style={{"padding-top": "12p", "padding-left": "16px"}}>
                <img src={tuit.avatar_image} height="48px" width="48px"
                     style={{"border-radius": "50%", "float": "left"}}/>
                <div style={{"padding-left": "60px", "padding-right": "16px"}}>
                    <div style={{"width": "506px"}}>
                        <text style={{"color": "white",  "font-size": "15px"}}><strong>{tuit.handle}</strong></text>
                        <i className="fa-light fa-octagon-check" style={{"color": "white"}}> </i>
                        <text style={{"color": "#5c636a", "font-size": "12px"}}>  {tuit.time} </text>
                        <br/>
                        <text style={{"color": "white", "font-size": "15px"}}>{tuit.topic}
                            <a style={{"color": "dodgerblue"}}>{tuit.topic}</a>{tuit.topic}</text>
                    </div>
                </div>
                    <h1>I am Martin</h1>
            </div>


        </>


    );
}

export default TuitListItem;
