import tuits from "../tuit-list/tuits.json";



const tuitReducer =
    (state = [], action) => {
        const tuitsReducer = (state = [], action) => {
            switch (action.type) {
                case 'FIND_ALL_TUITS':
                    return action.tuits;
                default:
                    return state;
            }
        }
    }

export default tuitReducer;
